sNumber = ""

nOne   = 0
nTwo   = 0
nThree = 0
nFour  = 0
nFive  = 0

nCounter = 0
nLargest = 0
nLargestLoc = 0
while nCounter < len(sNumber) -5:
    nOne = int(sNumber[nCounter])
    nTwo = int(sNumber[nCounter + 1])
    nThree = int(sNumber[nCounter + 2])
    nFour = int(sNumber[nCounter + 3])
    nFive = int(sNumber[nCounter + 4])
    if(nLargest < nOne *nTwo*nThree*nFour*nFive):
        nLargestLoc = nCounter
        nLargest = nOne *nTwo*nThree*nFour*nFive
    nCounter = nCounter +1
print (sNumber[nLargestLoc:nLargestLoc+5])
print (nLargest)
